import React, { useEffect } from 'react';
import styles from './RocketStyle.css'; // Adjust the path based on your project structure

const Home = () => {
  useEffect(() => {
    function stars() {
      let count = 20;
      let scene = document.querySelector(`.${styles.container}`);
      if (!scene) return; // Check if the scene element is found before proceeding
      let i = 0;
      while (i < count) {
        let star = document.createElement('i');
        let x = Math.floor(Math.random() * window.innerWidth);
        let y = Math.floor(Math.random() * window.innerHeight);

        let duration = Math.random() * 1 + 0.5;

        star.style.left = x + 'px';
        star.style.top = y + 'px'; // Set the star's initial position randomly
        star.style.width = '1px';
        star.style.height = '50px';
        star.style.opacity = '1';
        star.style.animation = `shootingStar ${duration}s linear infinite`;

        scene.appendChild(star);
        i++;
      }
    }

    stars();
  }, []);

  return (
    <div>
      <h1>Home</h1>
      <div className={styles.container}>
        {/* The stars will be created dynamically */}
        <div className="rocket">
          <img src="rocket.png" alt="Rocket" />
        </div>
      </div>
    </div>
  );
};

export default Home;
